create table krud
(
    id         bigint auto_increment
        primary key,
    title      longtext    null,
    template   longtext    null,
    grid       bigint      null,
    form       bigint      null,
    actions    longtext    null,
    created_at datetime(3) null,
    updated_at datetime(3) null,
    deleted_at datetime(3) null
);

create table krud_templates
(
    id            bigint auto_increment
        primary key,
    template_name longtext null
);

create table occupations
(
    id       varchar(26)                   not null
        primary key,
    name     varchar(255)                  not null,
    code     varchar(255)                  not null,
    `order`  decimal                       not null,
    status   varchar(255) default 'active' not null,
    parentId varchar(26)                   null,
    rootId   varchar(26)                   null,
    platform varchar(26)  default ''       not null,
    constraint code
        unique (code),
    constraint name
        unique (name)
);

create table password_resets
(
    token      longtext     not null,
    wrong      bigint       not null,
    email      varchar(191) not null
        primary key,
    created_at datetime(3)  null
);

create table roles
(
    id           bigint auto_increment
        primary key,
    name         varchar(191) not null,
    display_name longtext     null,
    description  longtext     null,
    permissions  text         null,
    extra        text         null,
    menu         longtext     null,
    created_at   datetime(3)  null,
    updated_at   datetime(3)  null,
    deleted_at   datetime(3)  null,
    constraint name
        unique (name)
);

create table users
(
    id              int          null,
    created_at      datetime(3)  null,
    updated_at      datetime(3)  null,
    deleted_at      datetime(3)  null,
    status          longtext     null,
    role            bigint       null,
    login           varchar(191) not null,
    email           varchar(191) not null,
    register_number varchar(191) not null,
    avatar          text         null,
    bio             text         null,
    first_name      longtext     null,
    last_name       longtext     null,
    birthday        date         null,
    phone           longtext     null,
    gender          longtext     null,
    password        longtext     not null,
    constraint email
        unique (email),
    constraint login
        unique (login),
    constraint login_2
        unique (login),
    constraint login_3
        unique (login),
    constraint register_number
        unique (register_number)
);

create table vb_schemas
(
    id         bigint unsigned auto_increment
        primary key,
    name       longtext    null,
    `schema`   text        null,
    type       longtext    null,
    created_at datetime(3) null,
    updated_at datetime(3) null
);

create table vb_schemas_admin
(
    vb_id      bigint unsigned auto_increment
        primary key,
    name       longtext    null,
    `schema`   text        null,
    type       longtext    null,
    id         longtext    null,
    created_at datetime(3) null,
    updated_at datetime(3) null
);

create definer = root@`%` view vw_main_occupations as
select `oasis`.`occupations`.`id`       AS `id`,
       `oasis`.`occupations`.`name`     AS `name`,
       `oasis`.`occupations`.`code`     AS `code`,
       `oasis`.`occupations`.`order`    AS `order`,
       `oasis`.`occupations`.`status`   AS `status`,
       `oasis`.`occupations`.`parentId` AS `parentId`,
       `oasis`.`occupations`.`rootId`   AS `rootId`,
       `oasis`.`occupations`.`platform` AS `platform`
from `oasis`.`occupations`
where (`oasis`.`occupations`.`parentId` is null);

create definer = root@`%` view vw_sub_occupations as
select `oasis`.`occupations`.`id`       AS `id`,
       `oasis`.`occupations`.`name`     AS `name`,
       `oasis`.`occupations`.`code`     AS `code`,
       `oasis`.`occupations`.`order`    AS `order`,
       `oasis`.`occupations`.`status`   AS `status`,
       `oasis`.`occupations`.`parentId` AS `parentId`,
       `oasis`.`occupations`.`rootId`   AS `rootId`,
       `oasis`.`occupations`.`platform` AS `platform`,
       `oasis`.`vpo`.`name`             AS `parent_name`
from (`oasis`.`occupations`
         left join `oasis`.`vw_main_occupations` `vpo` on ((`oasis`.`occupations`.`parentId` = `oasis`.`vpo`.`id`)))
where (`oasis`.`occupations`.`parentId` is not null);

